update.foreach:
update TA_OFFICE_USECAR_APPLY
apply_dept_id= #{entity.applyDeptId,jdbcType=VARCHAR}, apply_user_name= #{entity.applyUserName,jdbcType=VARCHAR}, apply_date= #{entity.applyDate,jdbcType=NUMERIC}, start_place= #{entity.startPlace,jdbcType=VARCHAR}, arrived_place= #{entity.arrivedPlace,jdbcType=VARCHAR}, start_date= #{entity.startDate,jdbcType=NUMERIC}, passengers_name= #{entity.passengersName,jdbcType=VARCHAR}, passenger_number= #{entity.passengerNumber,jdbcType=NUMERIC}, contact_name= #{entity.contactName,jdbcType=VARCHAR}, contact_phone= #{entity.contactPhone,jdbcType=VARCHAR}, require_arrived_date= #{entity.requireArrivedDate,jdbcType=NUMERIC}, usecar_hours= #{entity.usecarHours,jdbcType=VARCHAR}, apply_reason= #{entity.applyReason,jdbcType=VARCHAR}, car_id= #{entity.carId,jdbcType=VARCHAR}, driver_id= #{entity.driverId,jdbcType=VARCHAR}, back_time= #{entity.backTime,jdbcType=NUMERIC}, start_km= #{entity.startKm,jdbcType=NUMERIC}, back_km= #{entity.backKm,jdbcType=NUMERIC}, refuel_money= #{entity.refuelMoney,jdbcType=NUMERIC}, business_key= #{entity.businessKey,jdbcType=VARCHAR}, apply_dept_name= #{entity.applyDeptName,jdbcType=VARCHAR}, apply_user_id= #{entity.applyUserId,jdbcType=VARCHAR}, status= #{entity.status,jdbcType=VARCHAR}, driver_name= #{entity.driverName,jdbcType=VARCHAR}, car_num= #{entity.carNum,jdbcType=VARCHAR}
