mapper.select:
select 

	from TA_OFFICE_USECAR_APPLY 
	where TA_OFFICE_USECAR_APPLY.oid in (

		select 
		TA_OFFICE_USECAR_APPLY.oid
	    from TA_OFFICE_USECAR_APPLY 

	        and TA_OFFICE_USECAR_APPLY.oid = #{entity.oid}
	        and TA_OFFICE_USECAR_APPLY.apply_dept_id = #{entity.applyDeptId}
	        and TA_OFFICE_USECAR_APPLY.apply_user_name = #{entity.applyUserName}
	        and TA_OFFICE_USECAR_APPLY.apply_date = #{entity.applyDate}
	        and TA_OFFICE_USECAR_APPLY.start_place = #{entity.startPlace}
	        and TA_OFFICE_USECAR_APPLY.arrived_place = #{entity.arrivedPlace}
	        and TA_OFFICE_USECAR_APPLY.start_date = #{entity.startDate}
	        and TA_OFFICE_USECAR_APPLY.passengers_name = #{entity.passengersName}
	        and TA_OFFICE_USECAR_APPLY.passenger_number = #{entity.passengerNumber}
	        and TA_OFFICE_USECAR_APPLY.contact_name = #{entity.contactName}
	        and TA_OFFICE_USECAR_APPLY.contact_phone = #{entity.contactPhone}
	        and TA_OFFICE_USECAR_APPLY.require_arrived_date = #{entity.requireArrivedDate}
	        and TA_OFFICE_USECAR_APPLY.usecar_hours = #{entity.usecarHours}
	        and TA_OFFICE_USECAR_APPLY.apply_reason = #{entity.applyReason}
	        and TA_OFFICE_USECAR_APPLY.car_id = #{entity.carId}
	        and TA_OFFICE_USECAR_APPLY.driver_id = #{entity.driverId}
	        and TA_OFFICE_USECAR_APPLY.back_time = #{entity.backTime}
	        and TA_OFFICE_USECAR_APPLY.start_km = #{entity.startKm}
	        and TA_OFFICE_USECAR_APPLY.back_km = #{entity.backKm}
	        and TA_OFFICE_USECAR_APPLY.refuel_money = #{entity.refuelMoney}
	        and TA_OFFICE_USECAR_APPLY.business_key = #{entity.businessKey}
	        and TA_OFFICE_USECAR_APPLY.apply_dept_name = #{entity.applyDeptName}
	        and TA_OFFICE_USECAR_APPLY.apply_user_id = #{entity.applyUserId}
	        and TA_OFFICE_USECAR_APPLY.status = #{entity.status}
	        and TA_OFFICE_USECAR_APPLY.driver_name = #{entity.driverName}
	        and TA_OFFICE_USECAR_APPLY.car_num = #{entity.carNum}


)
