mapper.select:
select 

	from td_sm_organization 
	where td_sm_organization.org_id in (

		select 
		td_sm_organization.org_id
	    from td_sm_organization 

	        and td_sm_organization.org_id = #{entity.orgId}
	        and td_sm_organization.org_sn = #{entity.orgSn}
	        and td_sm_organization.org_name = #{entity.orgName}
	        and td_sm_organization.org_showname = #{entity.orgShowname}
	        and td_sm_organization.parent_id = #{entity.parentId}
	        and td_sm_organization.path = #{entity.path}
	        and td_sm_organization.layer = #{entity.layer}
	        and td_sm_organization.children = #{entity.children}
	        and td_sm_organization.code = #{entity.code}
	        and td_sm_organization.jp = #{entity.jp}
	        and td_sm_organization.qp = #{entity.qp}
	        and td_sm_organization.creatingtime = #{entity.creatingtime}
	        and td_sm_organization.creator = #{entity.creator}
	        and td_sm_organization.orgnumber = #{entity.orgnumber}
	        and td_sm_organization.orgdesc = #{entity.orgdesc}
	        and td_sm_organization.chargeorgid = #{entity.chargeorgid}
	        and td_sm_organization.org_level = #{entity.orgLevel}
	        and td_sm_organization.org_xzqm = #{entity.orgXzqm}
	        and td_sm_organization.isdirectlyparty = #{entity.isdirectlyparty}
	        and td_sm_organization.isforeignparty = #{entity.isforeignparty}
	        and td_sm_organization.isjichaparty = #{entity.isjichaparty}
	        and td_sm_organization.isdirectguanhu = #{entity.isdirectguanhu}
	        and td_sm_organization.remark1 = #{entity.remark1}
	        and td_sm_organization.remark2 = #{entity.remark2}
	        and td_sm_organization.remark3 = #{entity.remark3}
	        and td_sm_organization.remark4 = #{entity.remark4}
	        and td_sm_organization.remark5 = #{entity.remark5}
	        and td_sm_organization.remark6 = #{entity.remark6}
	        and td_sm_organization.remark7 = #{entity.remark7}
	        and td_sm_organization.remark8 = #{entity.remark8}
	        and td_sm_organization.remark9 = #{entity.remark9}
	        and td_sm_organization.remark10 = #{entity.remark10}


)

