and file_id = #{fileId} and file_id =

mapper.select:
select 

	from KCOMP_FILE_UPLOAD 
	where KCOMP_FILE_UPLOAD.file_id in (

		select 
		KCOMP_FILE_UPLOAD.file_id
	    from KCOMP_FILE_UPLOAD 

	        and KCOMP_FILE_UPLOAD.file_id = #{entity.fileId}
	        and KCOMP_FILE_UPLOAD.file_path = #{entity.filePath}
	        and KCOMP_FILE_UPLOAD.file_name = #{entity.fileName}
	        and KCOMP_FILE_UPLOAD.file_size = #{entity.fileSize}
	        and KCOMP_FILE_UPLOAD.file_type = #{entity.fileType}
	        and KCOMP_FILE_UPLOAD.business_type = #{entity.businessType}
	        and KCOMP_FILE_UPLOAD.business_key = #{entity.businessKey}
	        and KCOMP_FILE_UPLOAD.task_id = #{entity.taskId}
	        and KCOMP_FILE_UPLOAD.store_type = #{entity.storeType}
	        and KCOMP_FILE_UPLOAD.upload_time = #{entity.uploadTime}
	        and KCOMP_FILE_UPLOAD.org_id = #{entity.orgId}
	        and KCOMP_FILE_UPLOAD.user_id = #{entity.userId}
	        and KCOMP_FILE_UPLOAD.file_describe = #{entity.fileDescribe}
	        and KCOMP_FILE_UPLOAD.remark1 = #{entity.remark1}
	        and KCOMP_FILE_UPLOAD.remark2 = #{entity.remark2}


)

