mapper.select:
select 

	from td_sm_user 
	where td_sm_user.user_id in (

		select 
		td_sm_user.user_id
	    from td_sm_user 

	        and td_sm_user.user_id = #{entity.userId}
	        and td_sm_user.user_name = #{entity.userName}
	        and td_sm_user.user_password = #{entity.userPassword}
	        and td_sm_user.user_realname = #{entity.userRealname}
	        and td_sm_user.user_pinyin = #{entity.userPinyin}
	        and td_sm_user.user_sex = #{entity.userSex}
	        and td_sm_user.user_hometel = #{entity.userHometel}
