mapper.update:
update TA_OFFICE_ARCHHANDLE
cc_form_instanceid=#{ccFormInstanceid,jdbcType=VARCHAR}, audit_time=#{auditTime,jdbcType=TIMESTAMP}, opinion_content=#{opinionContent,jdbcType=VARCHAR}, logical_delete=#{logicalDelete,jdbcType=NUMERIC}, activity_name=#{activityName,jdbcType=VARCHAR}, proc_ins_id=#{procInsId,jdbcType=VARCHAR}, activity_id=#{activityId,jdbcType=VARCHAR}, business_key=#{businessKey,jdbcType=VARCHAR}, audit_state=#{auditState,jdbcType=VARCHAR}, auditer_id=#{auditerId.userId,jdbcType=VARCHAR}, audit_org_id=#{auditOrgId.orgId,jdbcType=VARCHAR}
and oid = #{oid} and oid =

mapper.select:
select 
	 
	from TA_OFFICE_ARCHHANDLE 
	
	where TA_OFFICE_ARCHHANDLE.oid in (

		select 
		TA_OFFICE_ARCHHANDLE.oid
	    from TA_OFFICE_ARCHHANDLE 
		 
	        and TA_OFFICE_ARCHHANDLE.oid = #{entity.oid}
	        and TA_OFFICE_ARCHHANDLE.cc_form_instanceid = #{entity.ccFormInstanceid}
	        and TA_OFFICE_ARCHHANDLE.audit_time = #{entity.auditTime}
	        and TA_OFFICE_ARCHHANDLE.opinion_content = #{entity.opinionContent}
	        and TA_OFFICE_ARCHHANDLE.logical_delete = #{entity.logicalDelete}
	        and TA_OFFICE_ARCHHANDLE.activity_name = #{entity.activityName}
	        and TA_OFFICE_ARCHHANDLE.proc_ins_id = #{entity.procInsId}
	        and TA_OFFICE_ARCHHANDLE.activity_id = #{entity.activityId}
	        and TA_OFFICE_ARCHHANDLE.business_key = #{entity.businessKey}
	        and TA_OFFICE_ARCHHANDLE.audit_state = #{entity.auditState}
		

)

update.foreach:
update TA_OFFICE_ARCHHANDLE
