module_id= #{entity.moduleId,jdbcType=VARCHAR}, business_key= #{entity.businessKey,jdbcType=VARCHAR}, business_key2= #{entity.businessKey2,jdbcType=VARCHAR}, read_permission= #{entity.readPermission,jdbcType=CHAR}, write_permission= #{entity.writePermission,jdbcType=CHAR}, fill_necessary= #{entity.fillNecessary,jdbcType=CHAR}, visible= #{entity.visible,jdbcType=CHAR}, field_id= #{entity.fieldId.fieldId,jdbcType=VARCHAR}
