mapper.select:
select 
	 
	from WORKFLOW_FORM_FIELD_PERMISSON 
	
	where WORKFLOW_FORM_FIELD_PERMISSON.id in (

		select 
		WORKFLOW_FORM_FIELD_PERMISSON.id
	    from WORKFLOW_FORM_FIELD_PERMISSON 
		 
	        and WORKFLOW_FORM_FIELD_PERMISSON.id = #{entity.id}
	        and WORKFLOW_FORM_FIELD_PERMISSON.module_id = #{entity.moduleId}
	        and WORKFLOW_FORM_FIELD_PERMISSON.business_key = #{entity.businessKey}
	        and WORKFLOW_FORM_FIELD_PERMISSON.business_key2 = #{entity.businessKey2}
	        and WORKFLOW_FORM_FIELD_PERMISSON.read_permission = #{entity.readPermission}
	        and WORKFLOW_FORM_FIELD_PERMISSON.write_permission = #{entity.writePermission}
	        and WORKFLOW_FORM_FIELD_PERMISSON.fill_necessary = #{entity.fillNecessary}
	        and WORKFLOW_FORM_FIELD_PERMISSON.visible = #{entity.visible}
		

)
