update.foreach:
update SERVICE_PROC_READING
send_user_id= #{entity.sendUserId,jdbcType=VARCHAR}, send_user_name= #{entity.sendUserName,jdbcType=VARCHAR}, receive_user_id= #{entity.receiveUserId,jdbcType=VARCHAR}, receive_user_name= #{entity.receiveUserName,jdbcType=VARCHAR}, receive_org_id= #{entity.receiveOrgId,jdbcType=VARCHAR}, receive_org_name= #{entity.receiveOrgName,jdbcType=VARCHAR}, proc_inst_id= #{entity.procInstId,jdbcType=VARCHAR}, business_key= #{entity.businessKey,jdbcType=VARCHAR}, read_status= #{entity.readStatus,jdbcType=NUMERIC}, sign_status= #{entity.signStatus,jdbcType=NUMERIC}, receipt_status= #{entity.receiptStatus,jdbcType=NUMERIC}, read_time= #{entity.readTime,jdbcType=NUMERIC}, sign_time= #{entity.signTime,jdbcType=NUMERIC}, receipt_time= #{entity.receiptTime,jdbcType=NUMERIC}, remark1= #{entity.remark1,jdbcType=VARCHAR}, remark2= #{entity.remark2,jdbcType=VARCHAR}, remark3= #{entity.remark3,jdbcType=VARCHAR}, remark4= #{entity.remark4,jdbcType=VARCHAR}, remark5= #{entity.remark5,jdbcType=VARCHAR}, send_activity_id= #{entity.sendActivityId,jdbcType=VARCHAR}, send_activity_name= #{entity.sendActivityName,jdbcType=VARCHAR}, send_module_id= #{entity.sendModuleId,jdbcType=VARCHAR}, send_module_name= #{entity.sendModuleName,jdbcType=VARCHAR}, send_time= #{entity.sendTime,jdbcType=TIMESTAMP}
