mapper.select:
select 

	from SERVICE_PROC_READING 
	where SERVICE_PROC_READING.id in (

		select 
		SERVICE_PROC_READING.id
	    from SERVICE_PROC_READING 

	        and SERVICE_PROC_READING.id = #{entity.id}
	        and SERVICE_PROC_READING.send_user_id = #{entity.sendUserId}
	        and SERVICE_PROC_READING.send_user_name = #{entity.sendUserName}
	        and SERVICE_PROC_READING.receive_user_id = #{entity.receiveUserId}
	        and SERVICE_PROC_READING.receive_user_name = #{entity.receiveUserName}
	        and SERVICE_PROC_READING.receive_org_id = #{entity.receiveOrgId}
	        and SERVICE_PROC_READING.receive_org_name = #{entity.receiveOrgName}
	        and SERVICE_PROC_READING.proc_inst_id = #{entity.procInstId}
	        and SERVICE_PROC_READING.business_key = #{entity.businessKey}
	        and SERVICE_PROC_READING.read_status = #{entity.readStatus}
	        and SERVICE_PROC_READING.sign_status = #{entity.signStatus}
	        and SERVICE_PROC_READING.receipt_status = #{entity.receiptStatus}
	        and SERVICE_PROC_READING.read_time = #{entity.readTime}
	        and SERVICE_PROC_READING.sign_time = #{entity.signTime}
	        and SERVICE_PROC_READING.receipt_time = #{entity.receiptTime}
	        and SERVICE_PROC_READING.remark1 = #{entity.remark1}
	        and SERVICE_PROC_READING.remark2 = #{entity.remark2}
	        and SERVICE_PROC_READING.remark3 = #{entity.remark3}
	        and SERVICE_PROC_READING.remark4 = #{entity.remark4}
	        and SERVICE_PROC_READING.remark5 = #{entity.remark5}
	        and SERVICE_PROC_READING.send_activity_id = #{entity.sendActivityId}
	        and SERVICE_PROC_READING.send_activity_name = #{entity.sendActivityName}
	        and SERVICE_PROC_READING.send_module_id = #{entity.sendModuleId}
	        and SERVICE_PROC_READING.send_module_name = #{entity.sendModuleName}
	        and SERVICE_PROC_READING.send_time = #{entity.sendTime}


)
