mapper.update:
update KCOMP_NOTICE_ANNOUNCEMENT
notify_no=#{notifyNo,jdbcType=VARCHAR}, notify_name=#{notifyName,jdbcType=VARCHAR}, notify_content=#{notifyContent,jdbcType=CLOB}, start_date=#{startDate,jdbcType=DATE}, end_date=#{endDate,jdbcType=DATE}, creator_id=#{creatorId,jdbcType=NUMERIC}, issuer_id=#{issuerId,jdbcType=VARCHAR}, issue_date=#{issueDate,jdbcType=DATE}, reviewer_id=#{reviewerId,jdbcType=NUMERIC}, is_reviewed=#{isReviewed,jdbcType=VARCHAR}, review_date=#{reviewDate,jdbcType=DATE}, state=#{state,jdbcType=NUMERIC}, remark=#{remark,jdbcType=VARCHAR}, org_id=#{orgId,jdbcType=VARCHAR}, user_id=#{userId,jdbcType=VARCHAR}, createdate=#{createdate,jdbcType=DATE}, mender_id=#{menderId,jdbcType=VARCHAR}, mender_name=#{menderName,jdbcType=VARCHAR}, mend_date=#{mendDate,jdbcType=DATE}, notify_type_id=#{notifyTypeId,jdbcType=VARCHAR}, scope_id=#{scopeId,jdbcType=VARCHAR}
and notify_id = #{notifyId} and notify_id =

mapper.select:
select 

	from KCOMP_NOTICE_ANNOUNCEMENT 
	where KCOMP_NOTICE_ANNOUNCEMENT.notify_id in (

		select 
		KCOMP_NOTICE_ANNOUNCEMENT.notify_id
	    from KCOMP_NOTICE_ANNOUNCEMENT 

	        and KCOMP_NOTICE_ANNOUNCEMENT.notify_id = #{entity.notifyId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.notify_no = #{entity.notifyNo}
	        and KCOMP_NOTICE_ANNOUNCEMENT.notify_name = #{entity.notifyName}
	        and KCOMP_NOTICE_ANNOUNCEMENT.start_date = #{entity.startDate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.end_date = #{entity.endDate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.creator_id = #{entity.creatorId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.issuer_id = #{entity.issuerId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.issue_date = #{entity.issueDate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.reviewer_id = #{entity.reviewerId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.is_reviewed = #{entity.isReviewed}
	        and KCOMP_NOTICE_ANNOUNCEMENT.review_date = #{entity.reviewDate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.state = #{entity.state}
	        and KCOMP_NOTICE_ANNOUNCEMENT.remark = #{entity.remark}
	        and KCOMP_NOTICE_ANNOUNCEMENT.org_id = #{entity.orgId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.user_id = #{entity.userId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.createdate = #{entity.createdate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.mender_id = #{entity.menderId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.mender_name = #{entity.menderName}
	        and KCOMP_NOTICE_ANNOUNCEMENT.mend_date = #{entity.mendDate}
	        and KCOMP_NOTICE_ANNOUNCEMENT.notify_type_id = #{entity.notifyTypeId}
	        and KCOMP_NOTICE_ANNOUNCEMENT.scope_id = #{entity.scopeId}


)

update.foreach:
