mapper.select:
select 

	from WORKFLOW_ACTIVITY_SETTING 
	where WORKFLOW_ACTIVITY_SETTING.id in (

		select 
		WORKFLOW_ACTIVITY_SETTING.id
	    from WORKFLOW_ACTIVITY_SETTING 

	        and WORKFLOW_ACTIVITY_SETTING.id = #{entity.id}
	        and WORKFLOW_ACTIVITY_SETTING.module_id = #{entity.moduleId}
	        and WORKFLOW_ACTIVITY_SETTING.task_def_id = #{entity.taskDefId}
	        and WORKFLOW_ACTIVITY_SETTING.action = #{entity.action}
	        and WORKFLOW_ACTIVITY_SETTING.inform_type = #{entity.informType}
	        and WORKFLOW_ACTIVITY_SETTING.needclaim = #{entity.needclaim}
	        and WORKFLOW_ACTIVITY_SETTING.is_sla_time = #{entity.isSlaTime}
	        and WORKFLOW_ACTIVITY_SETTING.include_tabs = #{entity.includeTabs}
	        and WORKFLOW_ACTIVITY_SETTING.include_actions = #{entity.includeActions}
	        and WORKFLOW_ACTIVITY_SETTING.return_activity = #{entity.returnActivity}
	        and WORKFLOW_ACTIVITY_SETTING.freechoose_url = #{entity.freechooseUrl}
	        and WORKFLOW_ACTIVITY_SETTING.remark1 = #{entity.remark1}
	        and WORKFLOW_ACTIVITY_SETTING.remark2 = #{entity.remark2}
	        and WORKFLOW_ACTIVITY_SETTING.remark3 = #{entity.remark3}
	        and WORKFLOW_ACTIVITY_SETTING.remark4 = #{entity.remark4}


)

