mapper.select:
select 

	from v_kcomp_work 
	where v_kcomp_work.work_id in (

		select 
		v_kcomp_work.work_id
	    from v_kcomp_work 

	        and v_kcomp_work.work_id = #{entity.workId}
	        and v_kcomp_work.work_no = #{entity.workNo}
	        and v_kcomp_work.work_title = #{entity.workTitle}
	        and v_kcomp_work.work_describe = #{entity.workDescribe}
	        and v_kcomp_work.applicant_id = #{entity.applicantId}
	        and v_kcomp_work.applicant_name = #{entity.applicantName}
	        and v_kcomp_work.applicant_contact_way = #{entity.applicantContactWay}
	        and v_kcomp_work.apply_branch_id = #{entity.applyBranchId}
	        and v_kcomp_work.apply_branch_name = #{entity.applyBranchName}
	        and v_kcomp_work.incident_level_id = #{entity.incidentLevelId}
	        and v_kcomp_work.urgent_level_id = #{entity.urgentLevelId}
	        and v_kcomp_work.affect_scope_id = #{entity.affectScopeId}
	        and v_kcomp_work.pri_id = #{entity.priId}
	        and v_kcomp_work.work_stage = #{entity.workStage}
	        and v_kcomp_work.work_state_id = #{entity.workStateId}
	        and v_kcomp_work.happen_date = #{entity.happenDate}
	        and v_kcomp_work.accept_date = #{entity.acceptDate}
	        and v_kcomp_work.assign_date = #{entity.assignDate}
	        and v_kcomp_work.business_key = #{entity.businessKey}
	        and v_kcomp_work.task_assignee = #{entity.taskAssignee}
	        and v_kcomp_work.task_def = #{entity.taskDef}
	        and v_kcomp_work.module_id = #{entity.moduleId}
	        and v_kcomp_work.service_type_id = #{entity.serviceTypeId}
	        and v_kcomp_work.service_type_name = #{entity.serviceTypeName}
	        and v_kcomp_work.proinsid = #{entity.proinsid}
	        and v_kcomp_work.prodefid = #{entity.prodefid}
	        and v_kcomp_work.task_id = #{entity.taskId}
	        and v_kcomp_work.is_recieve_timeout = #{entity.isRecieveTimeout}
	        and v_kcomp_work.is_handle_timeout = #{entity.isHandleTimeout}
	        and v_kcomp_work.sla_remaintime = #{entity.slaRemaintime}
	        and v_kcomp_work.type = #{entity.type}


)

